<?php defined('BASEPATH') OR exit('No direct script access allowed');class MY_Controller extends CI_Controller {    protected $data = array();    public function __construct() {        parent::__construct();        $this->load->model('LoginModel');    }    public function user() {        $user_data = new stdClass();        $user_id = $this->session->userdata('user_logged');        if ($user_id) {            $user_data = $this->LoginModel->get(array('id' => $user_id))->row();            return $user_data;        } else {            return FALSE;        }    }         public function user_type() {        $user_data = new stdClass();        $user_id = $this->session->userdata('user_logged');        if ($user_id) {            $user_data = $this->LoginModel->get_user_type(array('user_id' => $user_id))->row();            return $user_data;        } else {            return FALSE;        }    }    public function permission() {        $permission_data = new stdClass();        $user_id = $this->session->userdata('user_logged');        if ($user_id) {            $permission_data = $this->LoginModel->get_permission_list($user_id);            return $permission_data;        } else {            return FALSE;        }    }        public function set_view($page , $data = '') {        $this->data['this_user'] = $this->user();        $this->data['user_type'] = $this->user_type()->group_id;        $this->load->view('templates/header', $this->data);        $this->load->view($page, $data);        $this->load->view('templates/footer');    }}