<?php defined('BASEPATH') OR exit('No direct script access allowed');class MY_Controller extends CI_Controller {    protected $data = array();    public function __construct() {        parent::__construct();        $this->load->model('LoginModel');    }    public function user() {        $user_data = new stdClass();        $user_id = $this->session->userdata('user_logged');        if ($user_id) {            $user_data = $this->LoginModel->get(array('id' => $user_id))->row();            return $user_data;        } else {            return FALSE;        }    }    public function permission() {        $permission_data = new stdClass();        $user_id = $this->session->userdata('user_logged');        if ($user_id) {            $permission_data = $this->LoginModel->get_permission_list($user_id);            return $permission_data;        } else {            return FALSE;        }    }}